css:
.calendar-widget {
    background: linear-gradient(135deg, #a8edea 0%, #fed6e3 100%);
    border-radius: 15px;
    padding: 20px;
    color: #333;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    min-width: 280px;
}

.calendar-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
}

.nav-btn {
    background: rgba(255, 255, 255, 0.5);
    color: #333;
    border: none;
    border-radius: 50%;
    width: 30px;
    height: 30px;
    cursor: pointer;
    font-size: 18px;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: background 0.2s;
}

.nav-btn:hover {
    background: rgba(255, 255, 255, 0.8);
}

.month-year {
    font-weight: bold;
    font-size: 16px;
    text-align: center;
}

.calendar-grid {
    display: grid;
    grid-template-columns: repeat(7, 1fr);
    gap: 2px;
}

.day-header {
    text-align: center;
    font-weight: bold;
    font-size: 12px;
    padding: 5px;
    color: #666;
}

.day-cell {
    aspect-ratio: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    font-size: 14px;
    cursor: pointer;
    transition: all 0.2s;
    background: rgba(255, 255, 255, 0.3);
}

.day-cell:hover {
    background: rgba(255, 255, 255, 0.6);
}

.day-cell.today {
    background: #ff6b6b;
    color: white;
    font-weight: bold;
}

.day-cell.other-month {
    color: #999;
    background: rgba(255, 255, 255, 0.1);
}

js:
// Calendar Widget
let currentDate = new Date();
let currentMonth = currentDate.getMonth();
let currentYear = currentDate.getFullYear();

const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
];

function renderCalendar() {
    const firstDay = new Date(currentYear, currentMonth, 1);
    const lastDay = new Date(currentYear, currentMonth + 1, 0);
    const prevLastDay = new Date(currentYear, currentMonth, 0);

    const firstDayIndex = firstDay.getDay();
    const lastDayDate = lastDay.getDate();
    const prevLastDayDate = prevLastDay.getDate();

    // Update month/year display
    document.getElementById('month-year').textContent = 
        `${monthNames[currentMonth]} ${currentYear}`;

    let days = '';

    // Previous month days
    for (let x = firstDayIndex; x > 0; x--) {
        days += `<div class="day-cell other-month">${prevLastDayDate - x + 1}</div>`;
    }

    // Current month days
    const today = new Date();
    for (let i = 1; i <= lastDayDate; i++) {
        const isToday = 
            i === today.getDate() && 
            currentMonth === today.getMonth() && 
            currentYear === today.getFullYear();

        days += `<div class="day-cell ${isToday ? 'today' : ''}">${i}</div>`;
    }

    // Next month days
    const remainingDays = 42 - (firstDayIndex + lastDayDate);
    for (let j = 1; j <= remainingDays; j++) {
        days += `<div class="day-cell other-month">${j}</div>`;
    }

    document.getElementById('calendar-days').innerHTML = days;
}

function previousMonth() {
    currentMonth--;
    if (currentMonth < 0) {
        currentMonth = 11;
        currentYear--;
    }
    renderCalendar();
}

function nextMonth() {
    currentMonth++;
    if (currentMonth > 11) {
        currentMonth = 0;
        currentYear++;
    }
    renderCalendar();
}

function goToToday() {
    const today = new Date();
    currentMonth = today.getMonth();
    currentYear = today.getFullYear();
    renderCalendar();
}

// Initialize calendar
renderCalendar();

// Update calendar every minute to highlight today correctly
setInterval(() => {
    const today = new Date();
    if (currentMonth === today.getMonth() && currentYear === today.getFullYear()) {
        renderCalendar();
    }
}, 60000);
