css:
.countdown-widget {
    background: linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%);
    border-radius: 15px;
    padding: 20px;
    color: #333;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    text-align: center;
    min-width: 300px;
    position: relative;
}

.countdown-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    font-weight: bold;
    font-size: 16px;
}

.reset-btn {
    background: rgba(255, 255, 255, 0.5);
    color: #333;
    border: none;
    border-radius: 4px;
    padding: 4px 8px;
    font-size: 12px;
    cursor: pointer;
}

.reset-btn:hover {
    background: rgba(255, 255, 255, 0.8);
}

.countdown-display {
    display: flex;
    justify-content: space-around;
    margin-bottom: 20px;
}

.time-unit {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.time-value {
    font-size: 24px;
    font-weight: bold;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 8px;
    padding: 8px 12px;
    min-width: 50px;
    margin-bottom: 4px;
}

.time-label {
    font-size: 12px;
    color: #666;
    font-weight: 500;
}

.countdown-inputs {
    display: flex;
    gap: 8px;
    margin-bottom: 15px;
    justify-content: center;
}

.countdown-inputs input {
    width: 60px;
    padding: 8px;
    border: 1px solid rgba(255, 255, 255, 0.5);
    border-radius: 6px;
    background: rgba(255, 255, 255, 0.3);
    text-align: center;
    font-size: 14px;
}

.countdown-inputs input:focus {
    outline: none;
    border-color: #ff6b6b;
    background: rgba(255, 255, 255, 0.5);
}

.countdown-controls {
    display: flex;
    gap: 10px;
    margin-bottom: 15px;
    justify-content: center;
}

.countdown-controls button {
    padding: 8px 20px;
    border: none;
    border-radius: 20px;
    font-size: 14px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.2s;
}

#start-btn {
    background: #4ecdc4;
    color: white;
}

#start-btn:hover:not(:disabled) {
    background: #45b7b8;
}

#pause-btn {
    background: #ff6b6b;
    color: white;
}

#pause-btn:hover:not(:disabled) {
    background: #ff5252;
}

.countdown-controls button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

#event-input {
    width: 100%;
    padding: 8px;
    border: 1px solid rgba(255, 255, 255, 0.5);
    border-radius: 6px;
    background: rgba(255, 255, 255, 0.3);
    font-size: 14px;
    text-align: center;
}

#event-input:focus {
    outline: none;
    border-color: #ff6b6b;
    background: rgba(255, 255, 255, 0.5);
}

/* Alert Modal Styles */
.alert-modal {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 15px;
    z-index: 1000;
}

.alert-modal-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}

.alert-modal-content {
    background: linear-gradient(135deg, #4ecdc4 0%, #44a08d 100%);
    border-radius: 12px;
    padding: 20px;
    min-width: 280px;
    max-width: 90%;
    position: relative;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
    text-align: center;
    color: white;
}

.alert-modal-header {
    margin-bottom: 15px;
}

.alert-modal-header svg {
    opacity: 0.8;
}

.alert-modal-body h3 {
    margin: 0 0 8px 0;
    font-size: 18px;
    font-weight: bold;
}

.alert-modal-body p {
    margin: 0;
    font-size: 14px;
    opacity: 0.9;
}

.alert-modal-footer {
    margin-top: 20px;
}

.alert-btn {
    padding: 8px 20px;
    border: none;
    border-radius: 20px;
    font-size: 14px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.2s;
}

.alert-btn-primary {
    background: rgba(255, 255, 255, 0.9);
    color: #4ecdc4;
}

.alert-btn-primary:hover {
    background: white;
}

