// Countdown Timer Widget
let countdownInterval;
let totalSeconds = 0;
let isPaused = false;

function updateCountdownDisplay() {
    const days = Math.floor(totalSeconds / (24 * 60 * 60));
    const hours = Math.floor((totalSeconds % (24 * 60 * 60)) / (60 * 60));
    const minutes = Math.floor((totalSeconds % (60 * 60)) / 60);
    const seconds = totalSeconds % 60;

    document.getElementById('days').textContent = String(days).padStart(2, '0');
    document.getElementById('hours').textContent = String(hours).padStart(2, '0');
    document.getElementById('minutes').textContent = String(minutes).padStart(2, '0');
    document.getElementById('seconds').textContent = String(seconds).padStart(2, '0');
}

function startTimer() {
    if (isPaused) {
        // Resume from pause
        isPaused = false;
    } else {
        // Get input values
        const days = parseInt(document.getElementById('input-days').value) || 0;
        const hours = parseInt(document.getElementById('input-hours').value) || 0;
        const minutes = parseInt(document.getElementById('input-minutes').value) || 0;
        const seconds = parseInt(document.getElementById('input-seconds').value) || 0;

        totalSeconds = days * 24 * 60 * 60 + hours * 60 * 60 + minutes * 60 + seconds;

        if (totalSeconds === 0) {
            showAlert('Please set a valid time', 'Invalid Input');
            return;
        }
    }

    // Update UI
    document.getElementById('start-btn').disabled = true;
    document.getElementById('pause-btn').disabled = false;

    // Disable inputs
    document.querySelectorAll('.countdown-inputs input').forEach(input => {
        input.disabled = true;
    });

    // Start countdown
    countdownInterval = setInterval(() => {
        if (totalSeconds > 0) {
            totalSeconds--;
            updateCountdownDisplay();
        } else {
            // Timer finished
            clearInterval(countdownInterval);
            timerComplete();
        }
    }, 1000);
}

function pauseTimer() {
    clearInterval(countdownInterval);
    isPaused = true;

    document.getElementById('start-btn').disabled = false;
    document.getElementById('pause-btn').disabled = true;
    document.getElementById('start-btn').textContent = 'Resume';
}

function resetTimer() {
    clearInterval(countdownInterval);
    totalSeconds = 0;
    isPaused = false;

    // Reset display
    updateCountdownDisplay();

    // Reset UI
    document.getElementById('start-btn').disabled = false;
    document.getElementById('pause-btn').disabled = true;
    document.getElementById('start-btn').textContent = 'Start';

    // Enable inputs
    document.querySelectorAll('.countdown-inputs input').forEach(input => {
        input.disabled = false;
        input.value = '';
    });

    document.getElementById('event-input').value = '';
}

function timerComplete() {
    const eventName = document.getElementById('event-input').value || 'Timer';

    // Show completion modal
    document.getElementById('alert-title').textContent = `${eventName} Complete!`;
    document.getElementById('alert-message').textContent = 'Your countdown has finished.';
    document.getElementById('alert-modal').style.display = 'flex';

    // Change widget appearance
    const widget = document.querySelector('.countdown-widget');
    widget.style.background = 'linear-gradient(135deg, #4ecdc4 0%, #44a08d 100%)';
    widget.style.animation = 'pulse 1s ease-in-out 3';

    // Reset after showing completion
    setTimeout(() => {
        widget.style.background = 'linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)';
        widget.style.animation = '';
        resetTimer();
    }, 3000);
}

function showAlert(message, title = 'Notification') {
    document.getElementById('alert-title').textContent = title;
    document.getElementById('alert-message').textContent = message;
    document.getElementById('alert-modal').style.display = 'flex';
}

function closeAlertModal() {
    document.getElementById('alert-modal').style.display = 'none';
}

// Add pulse animation
const style = document.createElement('style');
style.textContent = `
    @keyframes pulse {
        0%, 100% { transform: scale(1); }
        50% { transform: scale(1.05); }
    }
`;
document.head.appendChild(style);

// Handle escape key for alert
document.addEventListener('keydown', function(e) {
    if (e.key === 'Escape' && document.getElementById('alert-modal').style.display !== 'none') {
        closeAlertModal();
    }
});

// Initialize display
updateCountdownDisplay();
