css:
.notes-widget {
    background: #fffacd;
    border: 2px solid #daa520;
    border-radius: 8px;
    padding: 10px;
    font-family: 'Comic Sans MS', cursive, sans-serif;
    box-shadow: 2px 2px 8px rgba(0, 0, 0, 0.1);
}

.notes-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 8px;
    font-weight: bold;
    color: #8b4513;
}

.clear-btn {
    background: #ff6b6b;
    color: white;
    border: none;
    border-radius: 4px;
    padding: 2px 8px;
    font-size: 11px;
    cursor: pointer;
}

.clear-btn:hover {
    background: #ff5252;
}

#notes-text {
    width: 100%;
    height: 150px;
    border: 1px solid #ddd;
    border-radius: 4px;
    padding: 8px;
    font-family: inherit;
    font-size: 13px;
    resize: none;
    background: rgba(255, 255, 255, 0.8);
}

#notes-text:focus {
    outline: none;
    border-color: #daa520;
}

js:
// Load saved notes on startup
const notesText = document.getElementById('notes-text');
if (notesText) {
    const savedNotes = localStorage.getItem('dashlayer-notes');
    if (savedNotes) {
        notesText.value = savedNotes;
    }

    // Auto-save notes
    notesText.addEventListener('input', () => {
        localStorage.setItem('dashlayer-notes', notesText.value);
    });
}

function clearNotes() {
    // Note: Widgets run in separate Tauri windows, so we use native confirm()
    if (confirm('Clear all notes?')) {
        notesText.value = '';
        localStorage.removeItem('dashlayer-notes');
        notesText.focus();
    }
}
