css:
.world-clock-widget {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 15px;
    padding: 20px;
    color: white;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
    min-width: 200px;
    position: relative;
}

.clock-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    font-weight: bold;
    font-size: 16px;
}

.add-btn {
    background: rgba(255, 255, 255, 0.2);
    color: white;
    border: none;
    border-radius: 50%;
    width: 24px;
    height: 24px;
    cursor: pointer;
    font-size: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.add-btn:hover {
    background: rgba(255, 255, 255, 0.3);
}

.clock-list {
    display: flex;
    flex-direction: column;
    gap: 12px;
}

.clock-item {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 8px;
    padding: 12px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: background 0.2s;
}

.clock-item:hover {
    background: rgba(255, 255, 255, 0.15);
}

.city-name {
    font-size: 14px;
    font-weight: 500;
}

.city-time {
    font-size: 16px;
    font-weight: bold;
    font-family: 'Courier New', monospace;
}

/* Inline Modal Styles */
.inline-modal {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 15px;
    z-index: 1000;
}

.inline-modal-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}

.inline-modal-content {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 12px;
    padding: 20px;
    min-width: 300px;
    max-width: 90%;
    position: relative;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.inline-modal-header h3 {
    margin: 0 0 15px 0;
    color: white;
    font-size: 18px;
    font-weight: bold;
}

.inline-modal-body {
    margin-bottom: 20px;
}

.inline-modal-body p {
    color: rgba(255, 255, 255, 0.9);
    margin: 0 0 12px 0;
    font-size: 14px;
}

.inline-hint {
    font-size: 12px;
    color: rgba(255, 255, 255, 0.7);
    margin-top: 8px;
}

.inline-input {
    width: 100%;
    padding: 10px;
    border: 1px solid rgba(255, 255, 255, 0.3);
    border-radius: 6px;
    background: rgba(255, 255, 255, 0.1);
    color: white;
    font-size: 14px;
    font-family: inherit;
}

.inline-input:focus {
    outline: none;
    border-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.2);
}

.inline-input::placeholder {
    color: rgba(255, 255, 255, 0.6);
}

.inline-modal-footer {
    display: flex;
    gap: 10px;
    justify-content: flex-end;
}

.inline-btn {
    padding: 8px 16px;
    border: none;
    border-radius: 6px;
    font-size: 14px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s;
}

.inline-btn-primary {
    background: rgba(255, 255, 255, 0.9);
    color: #667eea;
}

.inline-btn-primary:hover {
    background: white;
}

.inline-btn-secondary {
    background: rgba(255, 255, 255, 0.2);
    color: white;
}

.inline-btn-secondary:hover {
    background: rgba(255, 255, 255, 0.3);
}

js:
// World Clock Widget
const timezones = [
    { id: 'ny', timezone: 'America/New_York', name: 'New York' },
    { id: 'london', timezone: 'Europe/London', name: 'London' },
    { id: 'tokyo', timezone: 'Asia/Tokyo', name: 'Tokyo' }
];

let clockInterval;

function updateWorldClocks() {
    timezones.forEach(tz => {
        try {
            const now = new Date();
            const options = {
                timeZone: tz.timezone,
                hour: '2-digit',
                minute: '2-digit',
                hour12: false
            };
            const timeString = now.toLocaleTimeString('en-US', options);
            const element = document.getElementById(`time-${tz.id}`);
            if (element) {
                element.textContent = timeString;
            }
        } catch (error) {
            console.error(`Error updating ${tz.name}:`, error);
        }
    });
}

function addTimezone() {
    showInlineModal();
}

function showInlineModal() {
    document.getElementById('inline-modal').style.display = 'flex';
    document.getElementById('city-input-modal').focus();
}

function closeInlineModal() {
    document.getElementById('inline-modal').style.display = 'none';
    document.getElementById('city-input-modal').value = '';
}

function confirmAddCity() {
    const city = document.getElementById('city-input-modal').value.trim();
    if (!city) return;

    // Simple timezone mapping for common cities
    const timezoneMap = {
        'Paris': 'Europe/Paris',
        'Sydney': 'Australia/Sydney',
        'Dubai': 'Asia/Dubai',
        'Moscow': 'Europe/Moscow',
        'Beijing': 'Asia/Shanghai',
        'Singapore': 'Asia/Singapore',
        'Los Angeles': 'America/Los_Angeles',
        'Chicago': 'America/Chicago',
        'Mumbai': 'Asia/Kolkata',
        'Cairo': 'Africa/Cairo'
    };

    const timezone = timezoneMap[city];
    if (!timezone) {
        // Show inline error message
        const input = document.getElementById('city-input-modal');
        input.style.borderColor = '#ff6b6b';
        input.placeholder = 'City not supported! Try the listed cities';
        input.value = '';
        setTimeout(() => {
            input.style.borderColor = 'rgba(255, 255, 255, 0.3)';
            input.placeholder = 'City name';
        }, 2000);
        return;
    }

    const clockList = document.getElementById('clock-list');
    const clockItem = document.createElement('div');
    clockItem.className = 'clock-item';
    clockItem.dataset.timezone = timezone;
    clockItem.innerHTML = `
        <div class="city-name">${city}</div>
        <div class="city-time" id="time-${city.toLowerCase()}">--:--</div>
    `;

    clockList.appendChild(clockItem);

    // Add to timezones array
    timezones.push({
        id: city.toLowerCase(),
        timezone: timezone,
        name: city
    });

    closeInlineModal();
}

// Handle enter key in modal
document.addEventListener('keydown', function(e) {
    if (e.key === 'Enter' && document.getElementById('inline-modal').style.display !== 'none') {
        confirmAddCity();
    }
    if (e.key === 'Escape' && document.getElementById('inline-modal').style.display !== 'none') {
        closeInlineModal();
    }
});

// Initialize world clock
function initWorldClock() {
    updateWorldClocks();
    clockInterval = setInterval(updateWorldClocks, 1000);
}

// Start the world clock
initWorldClock();
